$dbConfig = array(
    'host' => 'localhost',
    'name' => 'touristtube',
    'user' => 'root',
    'pwd' => 'mysql_root'
);

$connection = 'mysql:host='.$dbConfig[ 'host' ].';dbname='.$dbConfig[ 'name' ];
$username   = $dbConfig[ 'user' ];
$password   = $dbConfig[ 'pwd' ];
$dbConn = new PDO($connection, $username, $password);
$dbConn->exec("set names utf8");

try
{
    $limit = 5000;
	for($i = 1;$i<=36;$i++)
	{
		$skip = ($i - 1) * 5000;
		$query = "SELECT * FROM `hrs_hotel` LIMIT $skip,5000";
		$select = $dbConn->prepare($query);
		$res    = $select->execute();
		//$data = db_fetch_array($res);
		//print_r($data);
		$data = $select->fetchAll(PDO::FETCH_ASSOC);
	//    print_r($data);
		foreach($data as $item){
			$source_id = $item['id'];
			$name = $item['name'];
			$street = $item['street'];
			$zip_code = $item['zip_code'];
			$city = $item['city'];
			$latitude = $item['latitude'];
			$longitude = $item['longitude'];
			$country = $item['country'];
			$country_iso_a2 = $item['country_iso_a2'];
			$stars = $item['category'];
			
			$insert_hotel_query = "INSERT INTO `cms_hotel`(`name`, `street`, `zip_code`, `city`, `latitude`, `longitude`, `country`, `country_iso_a2`, `stars`) VALUES"
					. " ('$name','$street','$zip_code','$city',$latitude,$longitude,'$country','$country_iso_a2',$stars)";
			$insert_hotel = $dbConn->prepare($insert_hotel_query);
			$insert_hotel->execute();
			$hotel_id = $dbConn->lastInsertId();
			
			$insert_hotel_source_query = "INSERT INTO `cms_hotel_source`(hotel_id, `source`, `source_id`) VALUES"
					. " ($hotel_id, 'hrs',$source_id)";
			$insert_hotel_source = $dbConn->prepare($insert_hotel_source_query);
			$insert_hotel_source->execute();
		}
	}
}
catch(Exception $ex)
{
    print_r($ex->getMessage());
}

echo 'done';
exit;


